<style type="text/css">
	.list-member{margin: 0 auto;}
	.list-member > .box{
		border: 1px solid #ddd;
		width: 120px;
		float: left;
		margin: 5px 10px;
	}
	.list-member > .box > .head > img{width: 100%;height: 140px;background: #eee}
	.list-member > .box > .body{padding: 2px}
	.list-member > .box > .body p{margin:0px;}
	.list-member > .box > .head .commit {
	    background: #0c7947b3;
	    color: #fff;
	    font-size: 11px;
	    position: relative;
	    margin-bottom: -19px;
	    text-align: center;
	    padding: 2px 2px;
	}
</style>
<script type="text/javascript">
	function fetchMemberDepartment() {
		var NIP = "<?=$detail->NIP?>";
        var data = {
            NIP : NIP
        };
        var token = jwt_encode(data,'UAP)(*');
        $.ajax({
            type : 'POST',
            url : base_url_js+"human-resources/fetch-member-department",
            data: {token:token},
            dataType : 'json',
            beforeSend :function(){
                loading_modal_show();
            },error : function(jqXHR){
            	loading_modal_hide();
                $("body #GlobalModal .modal-header").html("<h1>Error notification</h1>");
                $("body #GlobalModal .modal-body").html(jqXHR.responseText);
                $("body #GlobalModal").modal("show");
            },success : function(response){
            	loading_modal_hide();
                if(!jQuery.isEmptyObject(response)){
                	console.log(response);
                	$parentBox = $("#department-member .list-member");
                    $.each(response,function(k,v){
                    	$boxuser = $parentBox.find(".box:first").clone();
                    	$boxuser.find(".head .photo").attr("src","<?=base_url('uploads/employees/')?>"+v.Photo)
                    	.error(function(){
							$(this).attr("src","data:image/png;base64,");
						});
                    	$boxuser.find(".head > .commit").text(v.DivisionMain_+"-"+v.PositionMain_);
                    	$boxuser.find(".body > .info > .nip").text(v.NIP);
                    	$boxuser.find(".body > .info > .name").text(v.Name);
                		$parentBox.append($boxuser);
                    });
                    $parentBox.find(".box:first").remove();
                }
            }
        });
	}
    $(document).ready(function(){
    	fetchMemberDepartment();
        $("#form-employee .tabulasi-emp > ul > li").removeClass("active");
        $("#form-employee .tabulasi-emp > ul > li.nv-department").addClass("active");
    });
</script>
<div id="department-member">
	<div class="panel panel-primary">
        <div class="panel-heading">
            <h4 class="panel-title"><i class="fa fa-bars"></i> List employees from your department</h4>
        </div>
        <div class="panel-body">
        	<div class="list-member">
        		<div class="box">
    		 		<div class="head">
    		 			<p class="commit"><i class="fa fa-handshake-o"></i></p>
    		 			<img class="photo" src="data:image/png;base64,">
    		 		</div>
    		 		<div class="body">
    		 			<div class="info">
		 					<p class="nip text-center"></p>
    		 				<p class="name text-center">-</p>
    		 			</div>
    		 		</div>
		 		</div>
        	</div>
		</div>
	</div>
</div>