    function timeList() {
        var time = [];
        for (var i = 0; i < 24; i++) {
            var h=i;
            if(i < 10){ h= "0"+i; }
            for (var j = 0; j < 6; j++) {
                var m = j;
                if(j < 10){m=j+"0";}
                time.push(h+":"+m);
            };
        }
        return time;
    }

