                    }*/
                    if(totalRow == 1){
                        location.reload();
                    }
                }
            }else{
                if(totalRow == 1){
                    lastRow.find(".form-control, input, select,textarea").val("");
                }else{
                    lastRow.remove();
                }
            }
        });    

